import { useState } from "react";
import "./ControlButtons.css";

export default function ControlButton({selected}) {

    [uploadstatus, setUploadstatus] = useState();

    const fetchData = (value) => {
        const requestOptions = {
            method: 'PUT',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({"search": value})
        }; 


        fetch("http://127.0.0.1:5000/search", requestOptions)
        .then((response) => response.json())
        .then((json) => {
            // console.log(json);
            setResults(json);
        });
      }



    return (
        <div className="grid-item grid-item-4">
            <div className="upload-container">
                <button className="upload-button">Upload</button>
                <h1 className="status-label">{uploadstatus}</h1>
                <h2 className="status-upload">Not Uploaded</h2>
            </div>
            <div className="play-container">
                <button className="play-button">Play</button>
                <h1 className="status-label">Status</h1>
                <h2 className="play-upload">Not Playing</h2>
            </div>
            <div className="reset-container">
                <button className="reset-button">Reset</button>
                <h1 className="status-label">Status</h1>
                <h2 className="status-reset">Reseting</h2>
            </div>
        </div>
    )
}
