import ControlButton from './components/ControlButton';
import SearchBar from './components/SearchBar';
import SearchResultsList from './components/SearchResultsList';
import { useReducer, useState } from "react";

import './App.css';
import logo from "./images/logo_1.jpg";
import animation from "./images/stream.jpg";


export const ACTIONS = {

}

function App() {
  // remove for reducer soon
  const dispatch = [];

  const [input, setInput] = useState('');
  const [results, setResults] = useState([]);
  const [selected, setSelected] = useState(n)

  const handleChange = (value) => {
    setInput(value)
    if (value != "") {
        fetchData(value);
    } else {
        setResults([]);
    }
  }

  const handleSelect = (selectTerm) => {
    // api to fetch the search result
    console.log({"value": selectTerm});
  }

  const fetchData = (value) => {
    const requestOptions = {
        method: 'PUT',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({"search": value})
    }; 


    fetch("http://127.0.0.1:5000/search", requestOptions)
    .then((response) => response.json())
    .then((json) => {
        console.log(json);
        setResults(json);
    });
  }



  return (
    <div className="grid-container">
      <div className="grid-item grid-item-1">
        <img src={logo} alt="Traveling Pianst Logo"></img>
      </div>
      <div className="grid-item grid-item-2">
        <div className="search-inner">
          <input type='text' value={input} onChange={(e) => handleChange(e.target.value)} />
          <button onClick={()=>handleSelect(input)}>Select</button>
        </div>
        <div className="dropdown">
          {   
            results.map((result, id) => {
                return <div key={id}>{result.name}</div>
            })
          }
        </div>
      </div>
      <div className="grid-item grid-item-3">selcted song</div>
      <div className="grid-item grid-item-4">
        {/* <ControlButton label="Upload" dispatch={dispatch} />
        <ControlButton label="Play" dispatch={dispatch} />
        <ControlButton label="Reset" dispatch={dispatch} /> */}
        buttons
      </div>
      <div className="grid-item grid-item-5">animation</div>
    </div>
  );
}

export default App;
